<?php include_once('include/Controller/Controller.php');include_once('include/class_user.php');//// Базовый класс для печати//abstract class C_BasePrint extends Controller{	protected $title;		// ��������� ��������	protected $content;		// ���������� ��������	protected $id_article;	protected $id_forums;	protected $id_posts;	protected $msg_count;	private $check;	//	// �����������.	//	function __construct()	{		session_start();			}		//	// ����������� ���������� �������.	//	protected function OnInput()	{		$this->title = '�����';		$this->id_article = 'null';		$this->id_forums = 'null';		$this->id_threads = 'null';		$this->id_posts = 'null';		$this->content = '';				$mUsers = M_Users::Instance();				// ������� ������ ������.		$mUsers->ClearSessions();				// ������� ������������.		$this->user = $mUsers->Get();	}		//	// ����������� ��������� HTML.	//		protected function OnOutput()	{		$vars = array('title' => $this->title, 'content' => $this->content, 'id_article' => $this->id_article, 'msg_count' => $this->msg_count, 'id_forums' => $this->id_forums, 'id_thread' => $this->id_threads, 'id_posts' => $this->id_posts, 'user' => $this->user);			$page = $this->Template('templates/v_mainprint.php', $vars);						echo $page;	}	}